import * as React from 'react';
import { StyleSheet, Text, View ,TextInput,Button,ImageBackground} from 'react-native';
import LogInNav from '../navigators/loginNavigator';
import KeyboardSpacer from 'react-native-keyboard-spacer';

export default function ForgetPass({navigation}) {
    const cancelhandler=()=>{
        navigation.goBack();   
      }

    return (
        <View style={styles.container}>

            <ImageBackground style={styles.image} source={require("../images/2.jpg")}  >

      <View style={styles.Form}>
        
          <Text style={styles.Titles}>Set New Password</Text>
            
            <View>
            <Text style={styles.Title}>Email :</Text>
            <TextInput 
            style={styles.input}
            placeholder='Enter your email here..'
            //onChangeText={ChangeHandlerEmail} 
            />
            </View>
            
            <View>
            <Text style={styles.Title}>New Password :</Text>
            <TextInput 
            secureTextEntry={true}
            style={styles.input}
            placeholder='Password'
           // onChangeText={ChangeHandlerPass} 
            />
            </View>

            <View>
            <Text style={styles.Title}>Repeat Password :</Text>
            <TextInput 
            secureTextEntry={true}
            style={styles.input}
            placeholder='repeat Password'
           // onChangeText={ChangeHandlerPass} 
            />
            </View>
            
            <View>
            <Button 
             title='Change'
             color='#dc143c'
             />   

            <Button  
              onPress={()=> cancelhandler()}
              title='Cancel' 
              color='#dc143c'
              />
              </View>
   </View>

   </ImageBackground>
   <KeyboardSpacer/>
   </View>
  );
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        alignItems:"center",
        alignContent:"center",
        justifyContent:"center"
    },
    Form:{
        borderRadius:15,
        padding:10,
        paddingTop:40,
        paddingBottom:30,
        width:300,
        backgroundColor:'white',
        borderColor:'#dc143c',
        borderWidth:2,
        height:450,
        alignItems:"center",
        shadowColor: "#000",
        shadowOffset: {width: 0,height: 12,},
        shadowOpacity: 0.58,
        shadowRadius: 16.00,
        elevation: 24,      
    },
    input:{

        textAlign:'center',
        borderWidth:1,
        padding:15,
        borderRadius:15,
        marginBottom:10,
        borderColor:'black',
        borderBottomColor:'black',
        width:250,
    },
    Title:{
        textAlign:'center',
        fontSize:20,
        fontWeight:'bold',
    },
    Titles:{
        textAlign:'center',
        fontSize:30,
        fontWeight:'bold',
        paddingBottom:30,
        color:'#dc143c',//crimson
    },
    image: {
        flex: 1,
        width: '100%',
        height: '100%',
        alignItems:"center",
        alignContent:"center",
        justifyContent:"center",
      },


  });
