import React,{useState} from 'react';
import KeyboardSpacer from 'react-native-keyboard-spacer';
import { StyleSheet, Text, View ,FlatList,TextInput,Button,ImageBackground,ScrollView,SafeAreaView} from 'react-native';


export default function TextInputCalc({first,second,third,onClick}) {
    const [total, setTotal] = useState(0);
    const [text1, onChangeText1] = React.useState(text1);
    const [text2, onChangeText2] = React.useState(text2);
  
  const cal=()=>{
setTotal(text1 * text2);
  }
   
  
 
    return(
        <View>

<View style={styles.row}>
 <TextInput style={styles.input} placeholder={first} />
<TextInput style={styles.input} keyboardType='numeric' placeholder={second} 
 onChangeText={text1 => onChangeText1(text1)}/>
<TextInput style={styles.input} keyboardType='numeric' placeholder={third} 
 onChangeText={text2 => onChangeText2(text2)}/>
            </View>

            <View style={styles.button}>
                <Button color='#dd4e4e' title='Calculate' onPress={()=> cal()}></Button>
                <Button color='#dd4e4e' title='Add Row' onPress={()=>onClick(first,second,third)} ></Button>
    <Text style={styles.inputs}>{total}</Text>
            </View>
               
        </View>
    );
}

const styles = StyleSheet.create({
    Title:{
        textAlign:'center',
        fontSize:15,
        padding:5,
        fontWeight:'bold',
        height:35,
    },
    button:{
        padding:10,
        flexDirection:'row',
        justifyContent:'space-between',
    },

    row:{
        padding:10,
        flex:1,
        flexDirection:'row',
        justifyContent:'space-between',
        borderBottomColor:'#dd4e4e',
        borderBottomWidth:1,
    },

    input:{
        textAlign:'center',
        height:30,
        width:110,
    },
    
    inputs:{
        backgroundColor:'white',
        textAlign:'center',
        borderWidth:1,
        borderRadius:15,
        borderColor:'black',
        height:40,
        width:110, 
    },
    })
