import React,{useState} from 'react';
import KeyboardSpacer from 'react-native-keyboard-spacer';
import { StyleSheet, Text, View ,TextInput,FlatList,ScrollView,SafeAreaView} from 'react-native';
import RowName from '../components/RowName';
import TextInputCalc from '../components/TextInputCalc';
import ButtonsCalc from '../components/ButtonsCalc';


import { Button } from 'react-native';

export default function Claculate() {

    const [rowTools,setrowTools]=useState('');
    const [rowMaterial,setrowMaterial]=useState('');
    const [rowEmployees,setrowEmployees]=useState('');

    const addrowclicked1=(text1,text2,price)=>{//on the buttotn 
        setrowTools((prevrows)=>{//to update state
          return [//so we can use the old rows
            {text1:text1,text2:text2,price:price, key:Math.random().toString()},//use another random generater 
            ...prevrows ,//spreading the prev to do adds the new ones 
         
          ]
        })
      }
  
    const addrowclicked2=(text1,text2,price)=>{//on the buttotn 
          setrowMaterial((prevrows)=>{//to update state
            return [//so we can use the old rows
              {text1:text1,text2:text2,price:price, key:Math.random().toString()},//use another random generater 
              ...prevrows //spreading the prev to do adds the new ones 
            ]
          })
        }
  
    const addrowclicked3=(text1,text2,price)=>{//on the buttotn 
          setrowEmployees((prevrows)=>{//to update state
            return [//so we can use the old rows
              {text1:text1,text2:text2,price:price, key:Math.random().toString()},//use another random generater 
              ...prevrows //spreading the prev to do adds the new ones 
            ]
          })
        }
    

const [text1, setText1] = React.useState(text1);//keep tarck of user input 
const [text2, setText2] = React.useState(text2);//keep tarck of user input 

const ChangeHandler1 =(val)=>{ //when fire takes the value puts it in val 
    setText1(val);
 } 

const ChangeHandler2 =(val)=>{ //when fire takes the value puts it in val 
    setText2(val);
 } 


const [totalTools, setTotalTools] = useState(0);
const [totalMaterial, setTotalMaterial] = useState(0);
const [totalEmployee, setTotalEmployee] = useState(0);

const TotalTools=()=>{
    setTotalTools(text1*text2)
}

const removetools=(key)=>{//on the todo
    setrowTools((prevrow)=>{
      return prevrow.filter(rowTools => rowTools.key!=key);
    })
  }

const removematerial=(key)=>{//on the todo
    setrowMaterial((prevrow)=>{
      return prevrow.filter(rowMaterial => rowMaterial.key!=key);
    })
  }

const removeemployee=(key)=>{//on the todo
    setrowEmployees((prevrow)=>{
      return prevrow.filter(rowEmployees => rowEmployees.key!=key);
    })
  }




 
    return (
        <ScrollView >
            <View style={styles.container}>
                <View style={styles.Forms}>
                    <RowName Title={'Tools For Production'} first={'Tool Name'} second={'Quantity'} third={'Price'}/>
                    <FlatList 
                       data={rowTools} 
                       renderItem={(item)=>(   
                       <TextInputCalc item={item} first={'tool name'} second={'quantity'} third={'price'}
                        onClicks={()=>removetools()}/>
                    ) } />
                    <ButtonsCalc text1={text1} text2={text2} onClick={()=>addrowclicked1()} />
                </View>

                <View style={styles.Forms}>
                    <RowName Title={'Row Materials'} first={'Material Name'} second={'Quantity'} third={'Price'}/>
                    <FlatList data={rowMaterial}  
                       renderItem={(item)=>(
                       <TextInputCalc item={item} first={'material name'} second={'quantity'} third={'price'} 
                        onClicks={()=>removematerial()}/>
                    ) } />
                    <ButtonsCalc text1={text1} text2={text2} onClick={()=>addrowclicked2()} />
                </View>


                <View style={styles.Forms}>
                    <RowName Title={'Employment'} first={'Job Title'} second={'Number'} third={'Salary'}/>
                    <FlatList data={rowEmployees}  
                       renderItem={(item)=>(
                       <TextInputCalc item={item} first={'job title'} second={'number'} third={'salary'} 
                        onClicks={()=>removeemployee()}/>
                    ) } />
                    <ButtonsCalc text1={text1} text2={text2} onClick={()=>addrowclicked3()}/>
                </View>

                <View style={styles.Forms}>
                    
                        <RowName Title={'Other'}/>
                        <View style={styles.row}>
                           <Text>Energy (electrical, fuel)</Text>
                           <TextInput placeholder='Value'/>
                        </View>

                        <View style={styles.row}>
                           <Text>transportation</Text>
                           <TextInput placeholder='Value'/>
                        </View>
                    
                </View>


            </View>
            <KeyboardSpacer/>
    </ScrollView>
          );
      }

      const styles = StyleSheet.create({
        container:{
            paddingTop:40, 
            paddingBottom:40,
            backgroundColor:'white',
            flexDirection:'column',
            alignItems:'center',
            marginBottom:50,
        },
    
        row:{
            padding:10,
            flex:1,
            flexDirection:'row',
            justifyContent:'space-between',
            borderBottomColor:'#dd4e4e',
            borderBottomWidth:1,
        },
    
        Forms:{
            borderRadius:15,
            padding:10,
            paddingTop:30,
            paddingBottom:30, 
            backgroundColor:'white',
            height:'auto',
            shadowColor: "#000",
            shadowOffset: {	width: 0,	height: 5,},
            shadowOpacity: 0.28,
            shadowRadius: 5.00,
            elevation: 4,
            marginBottom:20,
            width:'94%',
            backgroundColor:'lightgray'
        },
        row:{
            padding:10,
            flex:1,
            flexDirection:'row',
            justifyContent:'space-between',
            borderBottomColor:'#dd4e4e',
            borderBottomWidth:1,
        },
    
        input:{
            textAlign:'center',
            height:30,
            width:110,
        },
        })
