import 'react-native-gesture-handler'
import React,{useState} from 'react';
import {  FlatList} from 'react-native';
import { StyleSheet, Text, View ,ScrollView,Button} from 'react-native';
import { Avatar,Overlay} from 'react-native-elements';
import ViewProduct from '../appScreens/ViewProduct';

import { connect } from 'react-redux';

export default function ProductPost () {  

  const [wish,setWish]=useState('');

    const [itemName1,SetItemName1]=useState('this name1');
    const [itemName2,SetItemName2]=useState('this name2');
    const [visible, setVisible] = useState(false);
    const toggleOverlay=()=>{
        setVisible(!visible);
    }
  
  const addToWish=()=>{
    setWish((prevWish)=>{
      return [
        { key:Math.random().toString()},
        ...prevWish //spreading the prev to do adds the new ones 
      ]
    })
}

  return(
   <ScrollView >

    <View  style={{width: 'auto', height: 'auto',alignItems:'center'}}>

   <View style={styles.around}>
   <View style={styles.Form}>

     <View style={{alignItems:'center'}}><Text >{itemName1} </Text></View>

      <View style={{marginLeft:10}}>
        <Avatar 
         title="PP"
          size="large"
          rounded={false}
          overlayContainerStyle={{backgroundColor: 'gray'}}
          activeOpacity={0.1}>
        </Avatar> 
      </View>
        
        <Button color='#dc143c' title='View' onPress={toggleOverlay} />
     
      </View>
      
        
      <View style={styles.Form}>

      <View style={{alignItems:'center'}}><Text >{itemName2} </Text></View>

      <View style={{marginLeft:10}}>
        <Avatar 
          title="PP"
          size="large"
          rounded={false}
          overlayContainerStyle={{backgroundColor: 'gray'}}
           activeOpacity={0.1}>
        </Avatar> 
      </View>

      <Button color='#dc143c' title='View' onPress={toggleOverlay} />

      </View>

   </View>

 </View>


 


      <Overlay isVisible={visible} onBackdropPress={toggleOverlay}>
            <ViewProduct />
 
      </Overlay>

  </ScrollView>
    );
}

const styles = StyleSheet.create({
around:{
  width:'100%',
    flexDirection:'row',
    justifyContent:'space-around',
    alignContent:'space-around',
  },
Form:{
  borderRadius:10,
  backgroundColor:'white',
  borderColor:'#dc143c',
  borderWidth:1,
  shadowColor: "#000",
  shadowOffset: {	width: 0,	height: 0,},
  shadowOpacity: 0.48,
  shadowRadius: 5.00,
  marginTop:10,
  marginBottom:5,
  elevation: 15,
  width: '46%',
   height: 'auto',
   paddingTop:10,
   //marginLeft:5,
  // marginRight:5
},

})
