import { StatusBar } from 'expo-status-bar';
import React,{useState} from 'react';
import { StyleSheet, Text, View, FlatList,Alert,TouchableWithoutFeedback,Keyboard} from 'react-native';
import Header from '../components/NoteHeader';
import TodoItem from '../components/NoteComp';
import AddTodo from '../components/NoteAdd';

export default function Notes() {
  const [todos,setTodos]=useState([

  ]);

  const pressHandler=(key)=>{//on the todo
    setTodos((prevTodos)=>{
      return prevTodos.filter(todos => todos.key!=key);
    })
  }

  const submitHandler=(text)=>{//on the butotn 
    if(text.length>3 ){
      setTodos((prevTodos)=>{
        return [
          {text:text, key:Math.random().toString()},
          ...prevTodos //spreading the prev to do adds the new ones 
        ]
      })
    }
    else{
      Alert.alert('opps!','more than 3 char ',[{text:'understood'}])
    }
  }

  return (
   // <SandBox/>
    <TouchableWithoutFeedback onPress={()=>{
      Keyboard.dismiss();
     }}>
    <View style={styles.container}>
      {/*header*/}
      <Header/>
      <View style={styles.content}>
        {/*to do form*/}
        <AddTodo submitHandler={submitHandler} /> 
        <View style={styles.list}>
          <FlatList
          data={todos}
          renderItem={({item})=>(
            <TodoItem item={item} pressHandler={pressHandler}/>
          ) } />
        </View>
      </View>
    </View>
          </TouchableWithoutFeedback> 
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  content:{
    flex: 1,
    padding:40,
  },
  list:{
    flex: 1,
    marginTop:20,
  }
});
