import React,{useState} from 'react';
import {ScrollView, Text,View,StyleSheet, FlatList} from 'react-native';
import { Divider } from 'react-native-elements';
import { Foundation } from '@expo/vector-icons'; 
import style from '../style.js';
import WishListComp from '../components/WishListComp';
import WishListNav from '../navigators/wishListNan';

export default function WishList (){ 

  const [wish,setwish]=('');

  const deletion=(key)=>{
    setwish((prevwishes)=>{
      return prevwishes.filter(todos => todos.key!=key);
    })
  }



  return (
   
    <ScrollView >
      

    <View style={styles.container}>

    <View style={{flexDirection:'row',justifyContent:'center'}}>
      <Text style={style.Titles} >Wish List  </Text>
      <Foundation name="heart" size={30} color="red" />
      <Foundation name="heart" size={20} color="red" />
    </View>

   <Divider style={{backgroundColor:'#dc143c',marginLeft:10,marginRight:10 ,marginBottom:10,}}/>

   <View style={styles.purchases}>
          <FlatList
          data={wish}
          renderItem={({item})=>(
            <WishListComp item={item} deletion={()=>deletion}/>
          ) } />
     
    </View>
    </View>
    

</ScrollView>

  );  
}  

const styles = StyleSheet.create({
  container:{
    margin:20,
    marginTop:25,
    justifyContent:'center',
  },

  purchases:{
    marginTop:5,
  },
})
