import React,{useState} from 'react';
import { View,Button,StyleSheet,Text} from 'react-native';
import {Overlay} from 'react-native-elements';
import { Avatar } from 'react-native-elements';
import ViewProduct from '../appScreens/ViewProduct';


export default function HomeComp () {  

    const [itemName,SetItemName]=useState('this name');
    const [visible, setVisible] = useState(false);
    const toggleOverlay=()=>{
        setVisible(!visible);
    }

    return(
        <View style={styles.Form} >
      
        <View style={{alignItems:'center',margin:10}}><Text >{itemName} </Text></View>
    
        <View style={{marginLeft:10,alignItems:'center'}}>
          <Avatar 
            title="PP"
            size="xlarge"
            rounded={false}
            overlayContainerStyle={{backgroundColor: 'gray'}}
            activeOpacity={0.1}>
         </Avatar> 
        </View>
      
        <Button color='#dc143c' title='View' onPress={toggleOverlay} />
         
        <Overlay isVisible={visible} onBackdropPress={toggleOverlay}>
          <ViewProduct />
      </Overlay>

        </View>
    );}

    const styles = StyleSheet.create({
        Form:{
          borderRadius:10,
          backgroundColor:'white',
          borderColor:'#dc143c',
          borderWidth:1,
          shadowColor: "#000",
          shadowOffset: {	width: 0,	height: 5,},
          shadowOpacity: 0.48,
          shadowRadius: 10.00,
          marginTop:10,
          marginBottom:5,
          elevation: 24,
          width: '94%',
           height: 'auto',
           paddingTop:10,
           //marginLeft:5,
          // marginRight:5
        },
        })
