import React,{useState} from 'react';
import { Text,View,TextInput,StyleSheet} from 'react-native';
import { Avatar,Icon } from 'react-native-elements';
import { AntDesign } from '@expo/vector-icons'; 
import { Foundation } from '@expo/vector-icons'; 
import style from '../style.js';


export default function MyPurchasesComp (){
    const [count, setCount] = useState(1);
    const [itemName,SetItemName]=useState('this name');
    const [Price, setPrice] = useState('0');
    
    return(
<View style={styles.Form}>

<View style={style.second}> 

<View style={{marginLeft:15}} >
   <Avatar 
    title="PP"
     size="large"
     rounded={false}
     overlayContainerStyle={{backgroundColor: 'gray'}}
     activeOpacity={0.1}>
   </Avatar> 
 </View>

<View style={{marginRight:15,justifyContent:"center",}}>
<View style={style.title}><Text >{itemName} </Text></View>
<View style={styles.itemsno}>
 <Text style={{marginTop:3,}}>{Price} </Text>
 <TextInput 
     placeholder='2'
     editable={false}
     placeholderTextColor={'black'}
   /> 
 <Foundation name="dollar" size={20} color="black" />
 </View>
 </View>

 </View>
 <View style={styles.header}>
 <Icon style={{marginTop:10,marginLeft:10}}name='delete' color='gray' size={25} /*onPress={deletion}*/ />
 <View style={styles.itemsno}>
  <AntDesign name="minus" size={25} color='#dd4e4e'  onPress={()=>setCount(count - 1)}/>
 <Text>{count}</Text>
 <Icon  style={{marginRight:10}} name='add' color='#dd4e4e' size={25}  onPress={()=>setCount(count + 1)}/>
 </View>

 </View>

</View>
    );}

const styles = StyleSheet.create({
  
    itemsno:{
      width:'auto',
      flexDirection:'row',
      marginTop:5,
      justifyContent:'center'
    },
  
    header:{
      flexDirection:'row',
      justifyContent:'space-between'
    },
  
    Form:{
      borderRadius:15,
      padding:10,
      paddingTop:30,
      paddingBottom:30, 
      backgroundColor:'white',
      height:'auto',
      shadowColor: "#000",
      shadowOffset: {	width: 0,	height: 5,},
      shadowOpacity: 0.28,
      shadowRadius: 5.00,
      elevation: 4,
      marginBottom:10
  },
  
  })
